const SET_DATA = "SET_DATA";
const SET_TEXT = "SET_TEXT";
const SET_XY = "SET_XY";
function setDATA(data) {
  return {
    type: SET_DATA,
    data
  };
}
function setTEXT(text) {
  return {
    type: SET_TEXT,
    text
  };
}
function setXY(data) {
  return {
    type: SET_XY,
    data
  };
}
// API Actions
function getDATA() {
  return (dispatch, getState) => {
    fetch("/crawl/getData/", {
    })
      .then(response => {
        return response.json();
      })
      .then(json => {
        
        dispatch(setDATA(json));
      })
      .catch(err => console.log(err));
  };
}
function getTEXT() {
  return (dispatch, getState) => {
    fetch("/crawl/getToWrite/", {
    })
      .then(response => {
        return response.json();
      })
      .then(json => {
        
        dispatch(setTEXT(json));
      })
      .catch(err => console.log(err));
  };
}
function postXY(data) {
  console.log("dd");
  return (dispatch, getState) => {
    const {user : { token }} = getState();
    fetch(`/crawl/setsoheeXY/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        'X-CSRF-TOKEN': token
      },
      body: JSON.stringify({
        data
      })
    })
      .then(response => {
        return response.json();
      })
      .then(json => {
        if (json.message) {
          console.log(json.message)
        }
      });
  };
}
// Initial State

const initialState = {};

// Reducer

function reducer(state = initialState, action) {
  switch (action.type) {
    case SET_DATA:
      return applySetDATA(state, action);
    case SET_TEXT:
        return applySetTEXT(state, action);
    case SET_XY:
      return applySetXY(state, action)
    default:
      return state;
  }
}
// Reducer Functions

function applySetDATA(state, action) {
  const { data } = action;
  return {
    ...state,
    data
  };
}
function applySetTEXT(state, action) {
  const { text } = action;
  return {
    ...state,
    text
  };
}
function applySetXY(state, action) {
  const { data } = action;
  return {
    ...state,
    data
  };
}
const actionCreators = {
  getDATA,
  getTEXT,
  postXY,
};

export { actionCreators };

// Export reducer by default

export default reducer;
