import PropTypes from "prop-types";
import Main from "./presenter";

class Container extends Component {
  redraw(){
    // Clear the entire canvas
    var p1 = ctx.transformedPoint(0,0);
    var p2 = ctx.transformedPoint(canvas.width,canvas.height);
          
    ctx.clearRect(p1.x,p1.y,p2.x-p1.x,p2.y-p1.y);
          
    // Alternatively:
    // ctx.save();
    // ctx.setTransform(1,0,0,1,0,0);
    // ctx.clearRect(0,0,canvas.width,canvas.height);
    // ctx.restore();
          if(first){
              ctx.font= '70px Calibri';
              ctx.fillText('Happy Birthday',0,70,1000);

              for(var i=0;i<1000;i++){
                  for(var j=0;j<1000;j++){
                      if(ctx.getImageData(i,j,1,1).data[3]==255){
                          //console.log(i,j);
                          imagearray.push([i,j]);
                          maxarray_x.push(i);
                          maxarray_y.push(j);
                          var randompick = makeRandom(1,6);
                          randomimage.push(randompick);
                      }           
                  }
              }
              var max_x = Math.max.apply(null,maxarray_x);
              var max_y = Math.max.apply(null,maxarray_y);
              var min_y = Math.min.apply(null,maxarray_y);
              y = document.body.clientHeight- ((max_y*(3000/max_x*(document.body.clientWidth/3168)))+  (min_y*(3000/max_x*(document.body.clientWidth/3168)))) -50 ;
              first=false;
              scale = 3000/max_x*(document.body.clientWidth/3168);
              zoomscale = scale;
              firstx = imagearray[0][0]*scale;
              firsty = imagearray[0][1]*scale;
          }
         
          ctx.clearRect(p1.x,p1.y,p2.x-p1.x,p2.y-p1.y);
          function makeRandom(min, max){
              var RandVal = Math.floor(Math.random()*(max-min+1)) + min;
              return RandVal;
          }
          console.log(lastX,lastY);
          for(i=0;i<imagearray.length;i++)
          {
              if(randomimage[i]==1)
              {
                  ctx.drawImage(image1,imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale,scale);
              }
              else if(randomimage[i]==2){
                  ctx.drawImage(image2,imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale,scale);
              }
              else if(randomimage[i]==3){
                  ctx.drawImage(image3,imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale,scale);
              }
              else if(randomimage[i]==4){
                  ctx.drawImage(image4,imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale,scale);
              }
              else if(randomimage[i]==5){
                  ctx.drawImage(image5,imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale,scale);
              }
              else if(randomimage[i]==6){
                  ctx.drawImage(image6,imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale,scale);
              }
              if(i==0){
                  console.log(imagearray[i][0]*scale,imagearray[i][1]*scale+y/2,scale);
              }
          }
          
              
    //
  }
  test(){
    var canvas = document.getElementsByTagName('canvas')[0];
	canvas.width = document.body.clientWidth ; canvas.height = window.innerHeight;
	var image1 = new Image;
    var image2 = new Image;
    var image3 = new Image;
    var image4 = new Image;
    var image5 = new Image;
    var image6 = new Image;
	var ball   = new Image;
    var first = true;
    
	window.onload = function(){		
		var ctx = canvas.getContext('2d');
		trackTransforms(ctx);
        var scale = ctx.scale;
        var imagearray = [];
        var maxarray_x = [];
        var maxarray_y = [];
        var randomimage = [];
        var y = 0;
        var zoomscale = 0;
        var firstx = 0;
        var gappos_x  = 0;
        var gappos_y = 0;
        var firsty = 0;
		
		redraw();
		canvas.addEventListener('dblclick', function(){ 

            var rect = canvas.getBoundingClientRect();
            var pt = ctx.transformedPoint(lastX,lastY);
            for(var i=0;i<imagearray.length;i++)
            {
                if(pt.x<imagearray[i][0]*scale+scale && pt.x>imagearray[i][0]*scale){
                    if(pt.y<imagearray[i][1]*scale+y/2+scale && pt.y>imagearray[i][1]*scale+y/2){
                        
                        if(randomimage[i]==1)
                        {
                            window.open("https://www.instagram.com/p/CP0UwV5l_au/", '_blank')
                        }
                        else if(randomimage[i]==2){
                            window.open("https://www.instagram.com/p/CPalK51lJyL/", '_blank')
                        }
                        else if(randomimage[i]==3){
                            window.open("https://www.instagram.com/p/CQDxvyxlgfT/", '_blank');
                        }
                        else if(randomimage[i]==4){
                            window.open("https://www.instagram.com/p/B8L65YjlWrF/", '_blank');
                        }
                        else if(randomimage[i]==5){
                            window.open("https://www.instagram.com/p/CEy7dMmFL75/", '_blank');
                        }
                        else if(randomimage[i]==6){
                            window.open("https://www.instagram.com/p/CS36-LeH7Fw/", '_blank');
                        }
                            
                        
                    }
                }
            }

        });
		var lastX=canvas.width/2, lastY=canvas.height/2;
		var dragStart,dragged;
		canvas.addEventListener('mousedown',function(evt){
			document.body.style.mozUserSelect = document.body.style.webkitUserSelect = document.body.style.userSelect = 'none';
			lastX = evt.offsetX || (evt.pageX - canvas.offsetLeft);
			lastY = evt.offsetY || (evt.pageY - canvas.offsetTop);
			dragStart = ctx.transformedPoint(lastX,lastY);
            gappos_x = lastX - firstx;
            gappos_y = lastY - firsty;
			dragged = false;
		},false);
		canvas.addEventListener('mousemove',function(evt){
			lastX = evt.offsetX || (evt.pageX - canvas.offsetLeft);
			lastY = evt.offsetY || (evt.pageY - canvas.offsetTop);
			dragged = true;
			if (dragStart){
				var pt = ctx.transformedPoint(lastX,lastY);
				ctx.translate(pt.x-dragStart.x,pt.y-dragStart.y);
				redraw();
			}
		},false);
		canvas.addEventListener('mouseup',function(evt){
			dragStart = null;

		},false);

		var scaleFactor = 1.1;
        
		var zoom = function(clicks){
			var pt = ctx.transformedPoint(lastX,lastY);
			ctx.translate(pt.x,pt.y);
			var factor = Math.pow(scaleFactor,clicks);
            zoomscale = zoomscale * factor;
			ctx.scale(factor,factor);
			ctx.translate(-pt.x,-pt.y);
			redraw();
		}
        
		var handleScroll = function(evt){
            
			var delta = evt.wheelDelta ? evt.wheelDelta/40 : evt.detail ? -evt.detail : 0;
            
			if (delta) zoom(delta);
            
			return evt.preventDefault() && false;
            
		};
		canvas.addEventListener('DOMMouseScroll',handleScroll,false);
		canvas.addEventListener('mousewheel',handleScroll,false);
	};
	image1.src = 'test_1.jpg';
    image1.href = 'https://www.instagram.com/p/CPalK51lJyL/';
    image2.src = 'test_2.jpg';
    image3.src = 'test_3.jpg';
    image4.src = 'test_4.jpg';
    image5.src = 'test_5.jpg';
    image6.src = 'test_6.jpg';
	ball.src   = 'http://phrogz.net/tmp/alphaball.png';
	
	// Adds ctx.getTransform() - returns an SVGMatrix
	// Adds ctx.transformedPoint(x,y) - returns an SVGPoint
	function trackTransforms(ctx){
		var svg = document.createElementNS("http://www.w3.org/2000/svg",'svg');
		var xform = svg.createSVGMatrix();
		ctx.getTransform = function(){ return xform; };
		
		var savedTransforms = [];
		var save = ctx.save;
		ctx.save = function(){
			savedTransforms.push(xform.translate(0,0));
			return save.call(ctx);
		};
		var restore = ctx.restore;
		ctx.restore = function(){
			xform = savedTransforms.pop();
			return restore.call(ctx);
		};

		var scale = ctx.scale;
		ctx.scale = function(sx,sy){
			xform = xform.scaleNonUniform(sx,sy);
			return scale.call(ctx,sx,sy);
		};
		var rotate = ctx.rotate;
		ctx.rotate = function(radians){
			xform = xform.rotate(radians*180/Math.PI);
			return rotate.call(ctx,radians);
		};
		var translate = ctx.translate;
		ctx.translate = function(dx,dy){
			xform = xform.translate(dx,dy);
			return translate.call(ctx,dx,dy);
		};
		var transform = ctx.transform;
		ctx.transform = function(a,b,c,d,e,f){
			var m2 = svg.createSVGMatrix();
			m2.a=a; m2.b=b; m2.c=c; m2.d=d; m2.e=e; m2.f=f;
			xform = xform.multiply(m2);
			return transform.call(ctx,a,b,c,d,e,f);
		};
		var setTransform = ctx.setTransform;
		ctx.setTransform = function(a,b,c,d,e,f){
			xform.a = a;
			xform.b = b;
			xform.c = c;
			xform.d = d;
			xform.e = e;
			xform.f = f;
			return setTransform.call(ctx,a,b,c,d,e,f);
		};
		var pt  = svg.createSVGPoint();
		ctx.transformedPoint = function(x,y){
			pt.x=x; pt.y=y;
			return pt.matrixTransform(xform.inverse());
		}
	}
  }
  componentDidMount() {
    this.test()
  }
  render() {
    return (
      <Main/>
    );
  }
}

export default Container;
